class Data {
  Data({
    int? totalCount,
    CurrentPosition? currentPosition,
    RegionInfo? regionInfo,
    List<HotelList>? hotelList,
  }) {
    _totalCount = totalCount;
    _currentPosition = currentPosition;
    _regionInfo = regionInfo;
    _hotelList = hotelList;
  }

  Data.fromJson(dynamic json) {
    _totalCount = json['totalCount'];
    _currentPosition = json['currentPosition'] != null
        ? CurrentPosition.fromJson(json['currentPosition'])
        : null;
    _regionInfo = json['regionInfo'] != null
        ? RegionInfo.fromJson(json['regionInfo'])
        : null;
    if (json['hotelList'] != null) {
      _hotelList = [];
      json['hotelList'].forEach((v) {
        _hotelList!.add(HotelList.fromJson(v));
      });
    }
  }

  int? _totalCount;
  CurrentPosition? _currentPosition;
  RegionInfo? _regionInfo;
  List<HotelList>? _hotelList;

  int? get totalCount => _totalCount;

  CurrentPosition? get currentPosition => _currentPosition;

  RegionInfo? get regionInfo => _regionInfo;

  List<HotelList>? get hotelList => _hotelList;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['totalCount'] = _totalCount;
    if (_currentPosition != null) {
      map['currentPosition'] = _currentPosition!.toJson();
    }
    if (_regionInfo != null) {
      map['regionInfo'] = _regionInfo!.toJson();
    }
    if (_hotelList != null) {
      map['hotelList'] = _hotelList!.map((v) => v.toJson()).toList();
    }
    return map;
  }
}

